height\":32,\"className\":\"desktop-only\"} -->\n<div style=\"height:32px\" aria-hidden=\"true\" class=\"wp-block-spacer desktop-only\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns {\"align\":\"wide\",\"className\":\"has-3-columns\"} -->\n<div class=\"wp-block-columns alignwide has-3-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":9,\"sizeSlug\":\"large\",\"className\":\"is-style-circle-mask\"} -->\n<div class=\"wp-block-image is-style-circle-mask\"><figure class=\"aligncenter size-large\"><img src=\"https://marinellayule.files.wordpress.com/2019/10/flowers.jpg?w=750\" alt=\"\" class=\"wp-image-9\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"align\":\"left\"} -->\n<h2 class=\"has-text-align-left\">TEFL-ed</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I received my Teaching English as a Foreign Language Certification in Prague, Czech Republic in 2016.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":12,\"sizeSlug\":\"large\",\"className\":\"is-style-circle-mask\"} -->\n<div class=\"wp-block-image is-style-circle-mask\"><figure class=\"aligncenter size-large\"><img src=\"https://marinellayule.files.wordpress.com/2019/10/clock.jpg?w=750\" alt=\"\" class=\"wp-image-12\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"align\":\"left\"} -->\n<h2 class=\"has-text-align-left\">Experienced</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I have taught ages from 4 to 50+ and to a variety of cultures such as Czech, Canadian, French, and different African ones. </p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":14,\"sizeSlug\":\"large\",\"className\":\"is-style-circle-m